graphql.query:
query Query {
  HealthCheck {
    LightWeight {
      runtime
      env {
        apigeeBaseUrl
        apigeeClientKey
        apigeeTokenUrl
        ccnBaseUrl
        cdpUrl
        investBaseUrl
        investCertPath
        investClientId
        env
        orionBaseUrl
        orionClientId
        orionUsername
        pegaClientId
        pegaBaseUrl
        secretsManagerName
      }
      version
      commit
    }
  }
}

query Query($input: IgnoreBy) {
  HealthCheck {
    HeavyWeight(input: $input) {
      errorCount
      pass
      fail
      inspection {
        id
        api
        url
        method
        endpoint
        status
        body
        json
        time
        error
      }
    }
  }
}

query CustomerSummary2($accountNumber: String, $lob: String, $cupid: String!, $userSessionId: String!) {
  customers(accountNumber: $accountNumber, lob: $lob) {
    total
    currentPage
    nextPage
    results {
      id
      sinceDate
      dob
      deceasedDate
      occupation
      status
      employer
      name {
        first
        middle
        prefix
        suffix
        last
      }
      preferredName {
        name
        prefix
        pronoun
      }
      phones {
        type
        value
        extension
        endDate
      }
      emails {
        type
        value
        endDate
      }
      addresses {
        type
        line1
        line2
        line3
        city
        zipCode
        state
        country
        careOf
        endDate
        lastUpdatedAt
      }
      profileVerification {
        version
        annualStatus
        annualBypass
        finalAnnualBypassDate
      }
      lobRelationships {
        lob
        id
        status
        statusDate
        relationships {
          type
          status
          statusDate
          active
        }
      }
      identifications {
        type
        number
        status
        expiryDate
      }
      restrictions {
        code
        accountNumber
        startDate
        description
      }
      consents {
        type
        version
        status
        date
      }
      meta {
        deliveryPreference
      }
    }
  }
  Customer {
    getCustomerCredentialBlocks(cupid: $cupid, userSessionId: $userSessionId) {
      online_access_status
      online_access_status_flag
      password_status
      password_status_flag
      mfa_status
    }
  }
}

