query Accounts($cif: String!, $input: cdActivitiesSearch) {
  Accounts {
    cdActivities(cif: $cif, input: $input) {
      conversationId
      cdActInfo {
        totalProjectedInterest
        cdMaturityDatePvtEncrypt
        lastRenewalDate
        ownershipType
        apy
        dailyInterestAccrued
        interestYtd
      }
    }
  }
}

query Accounts($cif: String!, $input: cdActivitiesSearch) {
