query Accounts($productType: String!) {
  Accounts {
    productRates(productType: $productType) {
      name
      productType
      description
      lob
      financialInstitutions {
        name
        availableTerms {
          name
          length {
            type
            value
          }
          description
          interestPostingFrequency {
            interestDisbursementOptions
            frequency
            defaultFrequency
          }
          rates {
            subType
            rateIndicator {
              lowerAmountLimit
              higherAmountLimit
            }
            apy
            type
            rate
          }
        }
        productIndicator {
          isPromotional
          isRetirement
        }
      }
    }
  }
}

